        public enum MapInfoObjType : uint      //储存特定数字代表那种地图物件
        {
            Blank = 0,
            Wall = 1,
            BirthPoint1 = 2,
            BirthPoint2 = 3,
            BirthPoint3 = 4,
            BirthPoint4 = 5,
            BirthPoint5 = 6,
            BirthPoint6 = 7,
            BirthPoint7 = 8,
            BirthPoint8 = 9
        }

        public const int numOfBirthPoint = 8;
        public static uint BirthPointEnumToIdx(MapInfoObjType birthPointEnum)
		{
            uint tmp = (uint)birthPointEnum;
            if (tmp < 2 || tmp > 9) throw new Exception("The parameter of BirthPointEnumToIdx is not a valid birth point enumeration value!");
            return tmp - 2;
		}
        public static bool ValidBirthPointIdx(uint birthPointIdx)
		{
            return birthPointIdx >= 0 && birthPointIdx < numOfBirthPoint;
		}

